import axios from "axios"
import { Link } from "react-router-dom"
import "./artiste.css"
import { useEffect, useState,useContext } from "react"
import { useLocation } from "react-router"

export default function Artiste({idArt,name1,dateNaiss1,dateDeces1,nationalite1,biographie1}) {
    const [name,setname] = useState(name1)
    const [dateNaiss,setdateNaiss] = useState(dateNaiss1)
    const [dateDeces,setdateDeces] = useState(dateDeces1)
    const [nationalite,setnationalite] = useState(nationalite1)
    const [biographie,setbiographie] = useState(biographie1)
    const [updateMode,setUpdateMode] = useState("")
    const [error, setError] = useState(false)

    const [artiste,setArtiste] = useState()
    const artisteId = localStorage.getItem(artId)

    useEffect(() => {
      const Artistedata = async () => {
        const { data } = await axios.get(`/artist/${artisteId}`);
        setArtiste(data);
      }
      Artistedata()
    },[])

    const handleUpdate = async (id) => {
      await axios.put(`/artist/${id}`);
      window.location.replace("/artistes")
   };



    const handleSubmit = async (e) => {
      e.preventDefault();
    try{
    const newartiste = await axios.post("/artist",{
        name,
        dateNaiss,
        dateDeces,
        nationalite,
        biographie
    });
    try{
      const res = await axios.post("/artist",newartiste);
      window.location.replace("/artistes")
      }catch(err) {}
      }  catch(err){
    console.log(err)
      }
  };

  return (
    <div className="artiste">
        <span className="artistetitle">Modify Artiste</span>
        <div className="form_class">
        <form  className="artisteform" onSubmit={handleSubmit}>
        <label >nom</label>
            <input className="artisteinput" value={artiste.nom} type="text" placeholder=" nom" 
             onChange={e=>setname(e.target.value)}
            />
            <label >date Naiss</label>
            <input className="artisteinput" value={artiste.dateNaiss}type="date" placeholder=" dateNaiss" 
             onChange={e=>setdateNaiss(e.target.value)}
            />
            <label >date Deces</label>
            <input className="artisteinput" type="date" placeholder=" date Deces" 
            onChange={e=>setdateDeces(e.target.value)}
            />
            <label >nationalite</label>
            <input className="artisteinput" type="text" placeholder=" nationalite" 
            onChange={e=>setnationalite(e.target.value)}
            />
            <label >biographie</label>
            <input className="artisteinput" type="text" placeholder="biographie" 
            onChange={e=>setbiographie(e.target.value)}
            />
            <button className="artistebutton1" type="submit">enregistrer</button>
        </form>
        </div>
    </div>
  )
}
