import axios from "axios"
import { Link } from "react-router-dom"
import "./acquisation.css"
import { useEffect, useState,useContext } from "react"
import { useLocation } from "react-router"


export default function Acquisation() {
    const [proprietaire,setproprietaire] = useState("")
    const [dateAcquisation,setdateAcquisation] = useState("")
    const [lieuAcquisation,setlieuAcquisation] = useState("")
    const [prix,setprix] = useState("")
    const [moyenAcquisation,setmoyenAcquisation] = useState("")
    const [preuveAchat,setpreuveAchat] = useState("")
    const [certification,setcertification] = useState("")
    const [updateMode,setUpdateMode] = useState("")


  const handleSubmit = async (e)=>{
    e.preventDefault();
    try{
    const res = await axios.post("/acquisation",{
        proprietaire,
        dateAcquisation,
        lieuAcquisation,
        prix,
        moyenAcquisation,
        preuveAchat,
        certification
    });
  }catch(err){
    console.log(err)
  }

  };

  return (
    <div className="acquisation">
        <span className="acquisationtitle">acquisation</span>
        <div className="form_class">
        <form  className="acquisationform" onSubmit={handleSubmit}>
            
            <label >proprietaire</label>
            <input className="acquisationinput" type="text" placeholder=" enter proprietaire..." 
             onChange={e=>setproprietaire(e.target.value)}
            />
            <label >date Acquisation</label>
            <input className="acquisationinput" type="text" placeholder=" dateAcquisation..." 
            onChange={e=>setdateAcquisation(e.target.value)}
            />
            <label >lieu Acquisation</label>
            <input className="acquisationinput" type="password" placeholder=" lieuAcquisation..." 
            onChange={e=>setlieuAcquisation(e.target.value)}

            />
            <button className="acquisationbutton1" type="submit">acquisation</button>
        </form>
        </div>
        //{err && <span style={{color:"red", marginTop:"10px"}}>somthing went wrong</span>}
    </div>
  )
}





