  <tr>
    <th>name</th>
    <th>date Naiss</th>
    <th>date Deces</th>
    <th>nationalite</th>
    <th>biographie</th>
    <th>Modifier</th>
    <th>Supprimer</th>

  </tr>


  
</table>

)}










    </div>
  );
}

export default Artistes;
