import axios from "axios"
import { Link } from "react-router-dom"
import "./redactionNoticeInventaire.css"
import { useEffect, useState,useContext } from "react"
import { useLocation } from "react-router"

export default function RedactionnoticeInventaire() {
    const [nomReducteur,setnomReducteur] = useState("")
    const [dateRedaction,setdateRedaction] = useState("")
    const [dateModification,setdateModification] = useState("")
    const [updateMode,setUpdateMode] = useState("")
    const [error, setError] = useState(false)

    const handleSubmit = async (e) => {
      e.preventDefault();
    try{
    const newredactionNoticeInventaire = await axios.post("/redactionNoticeInventaire",{
        nomReducteur,
        dateRedaction,
        dateModification
    });
    

    try{
      const res = await axios.post("/redactionNoticeInventaire",newredactionNoticeInventaire);
      window.location.replace("/")
      }catch(err) {}
      }  catch(err){
    console.log(err)
      }
  };

  return (
    <div className="redactionNoticeInventaire">
        <span className="redactionNoticeInventairetitle">redactionNoticeInventaire</span>
        <div className="form_class">
        <form  className="redactionNoticeInventaireform" onSubmit={handleSubmit}>
            <label >nom Reducteur</label>
            <input className="redactionNoticeInventaireinput" type="text" placeholder="nom Reducteur" 
             onChange={e=>setnomReducteur(e.target.value)}
            />
            <label >date Redaction</label>
            <input className="redactionNoticeInventaireinput" type="text" placeholder="dateRedaction" 
            onChange={e=>setdateRedaction(e.target.value)}
            />
            <label >date Modification</label>
            <input className="redactionNoticeInventaireinput" type="text" placeholder="date Modification" 
            onChange={e=>setdateModification(e.target.value)}
            />
            <button className="redactionNoticeInventairebutton1" type="submit">enregistrer</button>
        </form>
        </div>
    </div>
  )
}
