import "./singlepost.css"
import dev from "../assets/webdev.jpg"
import axios from 'axios'
import { useEffect, useState } from "react"
import { useLocation } from "react-router"
export default function Singlepost() {
  const location  = useLocation()
  const path = location.pathname.split("/")[2];
  const [post , setPost] = useState({})

  useEffect(()=>{
const getPost = async()=>{
  const res = await axios.get("/postes/"+path)
  clg
  setPost(res.data)
};
getPost()
  },[path])
  return (
    <div className="singlepost">
        <div className="singlepostwrapper">
          {post.photo &&
        <img src={post.photo} alt="" className="singlepostimg" />}
        <h1 className="singleposttitle">
            {post.title}
            <div className="singlepostedit">
            <i className="singleposteicon" id="first" class="fa-solid fa-pen-to-square"></i>
            <i className="singleposteicon" id="last" class="fa-solid fa-delete-left"></i>
            </div>
        </h1>
        <div className="singlepostinfo">
            <span className="singlepostauthor">Author :<b>{post.username}</b></span>
            <span className="singlepostdate">{new Date(post.createdAt).toDateString}</span>
        </div>
        <div className="singlepostdiscription">
        <p>
           {post.desc}
        </p>
        </div>
        </div>
    </div>
  )
}
