import axios from "axios";
import { useState } from "react";



export default function Localisaion() {
  const [lieu, setlieu] = useState("");
  const [placeDepot, setplaceDepot] = useState("");
  const [modeStockage, setmodeStockage] = useState("");
  const [updateMode, setUpdateMode] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newlocalisaionConservation = await axios.post("/localisaionConservation", {
        lieu,
        placeDepot,
        modeStockage,
      });
      try {
        const res = await axios.post("/localisaionConservation", newlocalisaionConservation);
        window.location.replace("/");
      } catch (err) { }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="localisaionConservation">
      <span className="localisaionConservationtitle">localisaionConservation</span>
      <div className="form_class">
        <form className="localisaionConservationform" onSubmit={handleSubmit}>
          <label>lieu</label>
          <input className="localisaionConservationinput" type="text" placeholder=" lieu"
            onChange={e => setlieu(e.target.value)} />
          <label>place Depot</label>
          <input className="localisaionConservationinput" type="text" placeholder="place Depot"
            onChange={e => setplaceDepot(e.target.value)} />
          <label>mode Stockage</label>
          <input className="localisaionConservationinput" type="text" placeholder="mode Stockage"
            onChange={e => setmodeStockage(e.target.value)} />
          <button className="localisaionConservationbutton1" type="submit">enregistrer</button>
        </form>
      </div>
    </div>
  );
}
