import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import "./artistes.css"


function Artistes() {

  const [Artistes, setArtistes] = useState([]);
  const {search} =useLocation();
  useEffect(() => {
  const Artistedata = async () => {
    const { data } = await axios.get('/artist'+search);
    setArtistes(data);
  }
  Artistedata()
},[search])
const handleDelete = async (id) => {
    await axios.delete(`/artist/${id}`);
    window.location.replace("/artist")
 };

  return (
 <div className='Artistes'>
<div id>
<Link className='link' to="/artiste">
<i class="fa-solid fa-plus"></i>
</Link>
</div>
<table id="customers">
  <tr>
    <th>date Naiss</th>
    <th>date Deces</th>
    <th>nationalite</th>
    <th>biographie</th>
    <th>Modifier</th>
    <th>Supprimer</th>

  </tr>
  {Artistes.map((art) => (
  <tr>
    <td>{art.dateNaiss}</td>
    <td>{art.dateDeces}</td>
    <td>{art.nationalite}</td>
    <td>{art.biographie}</td>
    <td>
<i className="singleposteicon" id="first" class="fa-solid fa-pen-to-square" ></i>
    </td>
    <td>
    <i className="singleposteicon" id="last" class="fa-solid fa-delete-left"></i>
    </td>
  </tr>
      ))}

  
  
</table>
    </div>
  );
}

export default Artistes;
