import axios from "axios"
import { Link } from "react-router-dom"
import "./artiste.css"
import { useEffect, useState,useContext } from "react"
import { useLocation } from "react-router"
/*
dateNaiss:{
        type:Date,
    },
    dateDeces:{
        type:Date,
    },
    nationalite:{
        type:String,
    },
    biographie:{
        type:String,
    }
    */

export default function Artiste() {
    const [dateNaiss,setdateNaiss] = useState("")
    const [dateDeces,setdateDeces] = useState("")
    const [nationalite,senationalite] = useState("")
    const [biographie,setprix] = useState("")
    const [moyenartiste,setmoyenartiste] = useState("")
    const [preuveAchat,setpreuveAchat] = useState("")
    const [certification,setcertification] = useState("")
    const [updateMode,setUpdateMode] = useState("")
    const [error, setError] = useState(false)



    const handleSubmit = async (e) => {
      e.preventDefault();
    try{
    const newartiste = await axios.post("/artiste",{
        proprietaire,
        dateartiste,
        lieuartiste,
        prix,
        moyenartiste,
        preuveAchat,
        certification
    });
    try{
      const res = await axios.post("/artiste",newartiste);
      window.location.replace("/")
      }catch(err) {}
      }  catch(err){
    console.log(err)
      }
  };

  return (
    <div className="artiste">
        <span className="artistetitle">artiste</span>
        <div className="form_class">
        <form  className="artisteform" onSubmit={handleSubmit}>
            
            <label >proprietaire</label>
            <input className="artisteinput" type="text" placeholder=" proprietaire" 
             onChange={e=>setproprietaire(e.target.value)}
            />
            <label >date artiste</label>
            <input className="artisteinput" type="text" placeholder=" date artiste" 
            onChange={e=>setdateartiste(e.target.value)}
            />
            <label >lieu artiste</label>
            <input className="artisteinput" type="text" placeholder=" lieu artiste" 
            onChange={e=>setlieuartiste(e.target.value)}
            />
            <label >prix</label>
            <input className="artisteinput" type="text" placeholder=" prix" 
            onChange={e=>setprix(e.target.value)}
            />
            <label >moyen artiste</label>
            <input className="artisteinput" type="text" placeholder=" moyen artiste" 
            onChange={e=>setmoyenartiste(e.target.value)}
            />
            <label >preuve Achat</label>
            <input className="artisteinput" type="text" placeholder="preuve Achat" 
            onChange={e=>setpreuveAchat(e.target.value)}
            />
            <label >certification</label>
            <input className="artisteinput" type="text" placeholder="certification" 
            onChange={e=>setcertification(e.target.value)}
            />

            <button className="artistebutton1" type="submit">enregistrer</button>
        </form>
        </div>
    </div>
  )
}
