import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import "./artistes.css"


function Artistes() {

  const [Artistes, setArtistes] = useState([]);
  const {search} =useLocation();
  useEffect(() => {
  const Artistedata = async () => {
    const { data } = await axios.get('/artist'+search);
    setArtistes(data);
  }
  Artistedata()
},[search])
const handleDelete  = async () => {
    try{
    await axios.delete(`/artist/`)
    window.location.replace("/artist")
    }
    catch(err){}
}
  return (
 <div className='Artistes'>

<table id="customers">
  <tr>
    <th>date Naiss</th>
    <th>date Deces</th>
    <th>nationalite</th>
    <th>biographie</th>
    <th>Modifier</th>
    <th>Supprimer</th>

  </tr>
  {Artistes.map((art) => (
  <tr>
    <td>{art.dateNaiss}</td>
    <td>{art.dateDeces}</td>
    <td>{art.nationalite}</td>
    <td>{art.biographie}</td>
    <td></td>
    <i className="singleposteicon" id="last" class="fa-solid fa-delete-left" onClick={handleDelete()}></i>
  </tr>
      ))}

  
  
</table>
    </div>
  );
}

export default Artistes;
